//START - MAIN PROGRAM

function showTemperatureConverter(){
    const mainContent = document.getElementById("main-content");
    mainContent.innerHTML = ""; // Clear any existing content
    TemperatureConverter(mainContent); // Render the Temperature Converter component
}

//END - MAIN PROGRAM

function TemperatureConverter(mainContent) {
    // Create heading
    const heading = document.createElement("h2");
    heading.textContent = "Temperature Converter";
    mainContent.appendChild(heading);

    // Create temperature input label and input
    const labelTemp = document.createElement("label");
    labelTemp.setAttribute("for", "temperature");
    labelTemp.textContent = "Enter Temperature:";
    mainContent.appendChild(labelTemp);

    const tempInput = document.createElement("input");
    tempInput.type = "number";
    tempInput.id = "temperature";
    tempInput.placeholder = "Enter value";
    mainContent.appendChild(tempInput);

    // Create unit selection label and select
    const labelUnit = document.createElement("label");
    labelUnit.setAttribute("for", "unit");
    labelUnit.textContent = "Select Unit:";
    mainContent.appendChild(labelUnit);

    const unitSelect = document.createElement("select");
    unitSelect.id = "unit";
    ["celsius", "fahrenheit", "kelvin"].forEach(unit => {
      const option = document.createElement("option");
      option.value = unit;
      option.textContent = unit.charAt(0).toUpperCase() + unit.slice(1) + (unit === "celsius" ? " (째C)" : unit === "fahrenheit" ? " (째F)" : " (K)");
      unitSelect.appendChild(option);
    });
    mainContent.appendChild(unitSelect);

    // Create output container
    const outputDiv = document.createElement("div");
    outputDiv.className = "output";
    outputDiv.innerHTML = `
      <p><strong>Celsius:</strong> <span id="celsiusResult">-</span> 째C</p>
      <p><strong>Fahrenheit:</strong> <span id="fahrenheitResult">-</span> 째F</p>
      <p><strong>Kelvin:</strong> <span id="kelvinResult">-</span> K</p>
    `;    
    mainContent.appendChild(outputDiv);

    const image = document.createElement("img");
    image.id = "temperatureImage";
    image.src = "data:image/jpeg;base64,"; // Replace with a valid image URL
    mainContent.appendChild(image)

    const celsiusResult = document.getElementById("celsiusResult");
    const fahrenheitResult = document.getElementById("fahrenheitResult");
    const kelvinResult = document.getElementById("kelvinResult");
    

    // Conversion logic
    function convertTemperature() {
      const value = parseFloat(tempInput.value);
      const unit = unitSelect.value;

      if (isNaN(value)) {
        celsiusResult.textContent = fahrenheitResult.textContent = kelvinResult.textContent = "-";
        return;
      }

      let celsius, fahrenheit, kelvin;

      switch (unit) {
        case "celsius":
          celsius = value;
          fahrenheit = (value * 9/5) + 32;
          kelvin = value + 273.15;
          break;
        case "fahrenheit":
          celsius = (value - 32) * 5/9;
          fahrenheit = value;
          kelvin = celsius + 273.15;
          break;
        case "kelvin":
          kelvin = value;
          celsius = value - 273.15;
          fahrenheit = (celsius * 9/5) + 32;
          break;
      }

      if (celsius > 25) {
        image.src = "data:image/jpeg;base64,";
      } else if (celsius < 8) {
        image.src = "data:image/jpeg;base64,"; // Replace with actual cold image URL
      } else {
        image.src = "data:image/jpeg;base64,"; // Replace with actual normal image URL
      }

      celsiusResult.textContent = celsius.toFixed(2);
      fahrenheitResult.textContent = fahrenheit.toFixed(2);
      kelvinResult.textContent = kelvin.toFixed(2);
    }

    tempInput.addEventListener("input", convertTemperature);
    unitSelect.addEventListener("change", convertTemperature);
}